	public void log(boolean isReturn, String obj, String cls, String fnc)
	{
		if (!isReturn)
		{
			tabulation++;
			
			for (int i = 0; i < tabulation; i++)
				System.out.print("\t|");
			
			System.out.print("->" + obj + ":" + cls + "." + fnc + "\n");
		}
		else
		{
			for (int i = 0; i < tabulation; i++)
				System.out.print("\t|");
			
			System.out.print("<-" + obj + ":" + cls + "\n");
			
			tabulation--;
		}
	}
	
	public Main() {
		tabulation = 0;
		menuState = 0;
	}
	
